import datetimex = 0listCase1 = []listCase2 = []listCase3 = []while x < 10:    start_time = datetime.datetime.now()    i = 0    while i < 1000:        age = 39        name = "John"        txt = f"His name is {name}. {name} is {age} years old."        print(txt)        i = i + 1    end_time = datetime.datetime.now()    listCase1.append(end_time - start_time)    start_time = datetime.datetime.now()    i = 0    while i < 1000:        age = 39        name = "John"        txt = "His name is {1}. {1} is {0} years old."        print(txt.format(age, name))        i = i + 1    end_time = datetime.datetime.now()    listCase2.append(end_time - start_time)    start_time = datetime.datetime.now()    i = 0    while i < 1000:        age = 39        name = "John"        txt = "His name is " + name + ". " + name + " is " + str(age) + " years old."        print(txt)        i = i + 1    end_time = datetime.datetime.now()    listCase3.append(end_time - start_time)    x = x + 1y = 0for result in listCase1:
    print("Run #" + str(y))
    print("First Case took: " + str(result))    print("Second Case took: " + str(listCase2[y]))    print("Third Case took: " + str(listCase3[y]))        if result > listCase2[y] and result > listCase3[y]:         print("First Case won.")    elif listCase2[y] > result and listCase2[y] > listCase3[y]:        print("Second Case won.")    elif listCase3[y] > result and listCase3[y] > listCase2[y]:        print("Third Case won.")    y = y + 1